JobDefinitionSteps.SelectClause:
DECLARE
info_ VARCHAR2(2000);
objid_ VARCHAR2(100);
objversion_ VARCHAR2(100);
attr_ VARCHAR2(32000);

ORDER_NO_ VARCHAR2(12) := ?;
CUSTOMER_NO_ VARCHAR2(10) := ?;
COMPANY_ VARCHAR2(20) := ?;
CURRENCY_ VARCHAR2(03) := ?;
CARDHOLDER_NAME_ VARCHAR2(100) := ?;
CREDIT_EXP_MONTH_ VARCHAR2(20) := ?;
CREDIT_EXP_YEAR_ VARCHAR2(20) := ?;
CARD_TYPE_ VARCHAR2(20) := ?;
CREDIT_CARD_NO_ VARCHAR2(4000) := ?;
DISPLAY_CARD_NUMBER_ VARCHAR2(100) := ?;
PN_REF_ VARCHAR2(100) := ?;
AUTH_CODE_ VARCHAR2(100) := ?;
EMPTY_VALUE_ VARCHAR2(100) := '';

BEGIN
  Client_SYS.Clear_Attr(attr_);
  Client_SYS.Add_To_Attr('ORDER_NO', ORDER_NO_, attr_);
  Client_SYS.Add_To_Attr('CUSTOMER_NO', CUSTOMER_NO_, attr_);
  Client_SYS.Add_To_Attr('COMPANY', COMPANY_, attr_);
  Client_SYS.Add_To_Attr('CURRENCY', CURRENCY_, attr_);
  Client_SYS.Add_To_Attr('CARDHOLDER_NAME', CARDHOLDER_NAME_, attr_); 

  Client_SYS.Add_To_Attr('CREDIT_EXP_MONTH', CREDIT_EXP_MONTH_, attr_);
  Client_SYS.Add_To_Attr('CREDIT_EXP_YEAR', CREDIT_EXP_YEAR_, attr_);
  Client_SYS.Add_To_Attr('CARD_TYPE', CARD_TYPE_, attr_);
  Client_SYS.Add_To_Attr('CREDIT_CARD_NO', CREDIT_CARD_NO_, attr_);
  Client_SYS.Add_To_Attr('DISPLAY_CARD_NUMBER', DISPLAY_CARD_NUMBER_, attr_);

  Order_Credit_Card_API.New__(info_, objid_, objversion_, attr_, 'DO');
  COMMIT;

  IFSAPP.CREDIT_CARD_TRANSACTIONS_API.Manual_Authorize(EMPTY_VALUE_ ,'CUSTOMERORDER' ,ORDER_NO_ ,PN_REF_ ,AUTH_CODE_ );
  COMMIT;
END;
